import React, { useState, useEffect } from "react";
import FitnessCenterIcon from "@mui/icons-material/FitnessCenter";
import AddIcon from "@mui/icons-material/Add";
import { Link } from "react-router-dom";
import ArrowBackIcon from "@mui/icons-material/ArrowBack";
import SearchIcon from "@mui/icons-material/Search";
import ChevronLeftIcon from "@mui/icons-material/ChevronLeft";
import ChevronRightIcon from "@mui/icons-material/ChevronRight";
import Model from "../../Components/Model/model";
import MemberCard from "../../Components/MemberCard/memberCard";
import AddmemberShip from "../../Components/Addmembership/addmemberShip";
import Addmembers from "../../Components/Addmembers/addmembers";
import axios from "axios";

const Member = () => {
  const [addMembership, setAddMembership] = useState(false);
  const [addMember, setAddMember] = useState(false);
  const [data, setData] = useState([]);
  const [search, setSearch] = useState("");
  const [isSearchModeOn, setIsSearchModeOn] = useState(false);
  const [totalData, setTotalData] = useState(0);
  const [limit] = useState(9);
  const [skip, setSkip] = useState(0);
  const [currentPage, setCurrentPage] = useState(1);
  const [startFrom, setStartFrom] = useState(0);
  const [endTo, setEndTo] = useState(9);
  const [noOfPage, setNoOfPage] = useState(0);

  useEffect(() => {
    fetchData(0, limit);
  }, []);

  const fetchData = async (skip, limit) => {
    try {
      const response = await axios.get(
        `http://localhost:4000/members/all-member?skip=${skip}&limit=${limit}`,
        { withCredentials: true }
      );

      const totalMembers = response.data.totalMembers;
      setTotalData(totalMembers);
      setData(response.data.members);

      let extraPage = totalMembers % limit === 0 ? 0 : 1;
      let totalPages = Math.floor(totalMembers / limit) + extraPage;
      setNoOfPage(totalPages);

      if (totalMembers === 0) {
        setStartFrom(-1);
        setEndTo(0);
      } else {
        setStartFrom(skip);
        setEndTo(Math.min(skip + limit, totalMembers));
      }
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  const handleMembers = () => setAddMember((prev) => !prev);
  const handleMemberShip = () => setAddMembership((prev) => !prev);

  const handleSearchData = () => {
    setIsSearchModeOn(search.length > 0);
    console.log("Searching for:", search);
  };

  const handlePrev = () => {
    if (currentPage > 1) {
      const prevPage = currentPage - 1;
      setCurrentPage(prevPage);
      const newSkip = (prevPage - 1) * limit;
      setSkip(newSkip);
      fetchData(newSkip, limit);
    }
  };

  const handleNext = () => {
    if (currentPage < noOfPage) {
      const nextPage = currentPage + 1;
      setCurrentPage(nextPage);
      const newSkip = nextPage * limit - limit;
      setSkip(newSkip);
      fetchData(newSkip, limit);
    }
  };

  return (
    <div className="text-black p-5 w-3/4 h-[100vh]">
      {/* Banner */}
      <div className="border-2 bg-gray-800 flex justify-between w-full text-white rounded-lg p-3">
        <button
          className="border-2 px-4 py-2 rounded-2xl cursor-pointer hover:bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500 hover:text-black"
          onClick={handleMembers}
        >
          Add Member <FitnessCenterIcon />
        </button>
        <button
          className="border-2 px-4 py-2 rounded-2xl cursor-pointer hover:bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500 hover:text-black"
          onClick={handleMemberShip}
        >
          Membership <AddIcon />
        </button>
      </div>

      {/* Back to Dashboard */}
      <Link to={"/dashboard"} className="flex items-center mt-3">
        <ArrowBackIcon /> Back to Dashboard
      </Link>

      {/* Search Bar */}
      <div className="mt-5 w-1/2 flex gap-2">
        <input
          type="text"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="border-2 w-full p-2 rounded-lg"
          placeholder="Search By Name or Mobile No"
        />
        <button
          onClick={handleSearchData}
          className="bg-slate-900 p-3 border-2 text-white rounded-lg cursor-pointer hover:bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500 hover:text-black"
        >
          <SearchIcon />
        </button>
      </div>

      {/* Members Count & Pagination */}
      <div className="mt-5 text-xl flex justify-between text-slate-900">
        <div>
          Total Members {isSearchModeOn ? `${totalData}` : null}
        </div>
        {!isSearchModeOn && totalData > 0 && (
          <div className="flex gap-5">
            <span>
              {startFrom + 1} - {endTo} of {totalData} Members
            </span>
            <button
              disabled={currentPage === 1}
              onClick={handlePrev}
              className={`w-8 h-8 border-2 flex items-center justify-center rounded-lg ${
                currentPage === 1
                  ? "bg-gray-200 text-gray-400 cursor-not-allowed"
                  : "hover:text-white hover:bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500"
              }`}
            >
              <ChevronLeftIcon />
            </button>
            <button
              disabled={currentPage === noOfPage}
              onClick={handleNext}
              className={`w-8 h-8 border-2 flex items-center justify-center rounded-lg ${
                currentPage === noOfPage
                  ? "bg-gray-200 text-gray-400 cursor-not-allowed"
                  : "hover:text-white hover:bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500"
              }`}
            >
              <ChevronRightIcon />
            </button>
          </div>
        )}
      </div>

      {/* Member Cards */}
      <div className="bg-slate-100 p-5 mt-5 rounded-lg grid gap-2 grid-cols-3 overflow-x-auto h-[65%]">
        {data.map((item, index) => (
          <MemberCard key={item.id || index} item={item} />
        ))}
      </div>

      {/* Modals */}
      {addMembership && (
        <Model
          header="Add Membership"
          handleClose={handleMemberShip}
          Content={<AddmemberShip handleClose={handleMemberShip} />}
        />
      )}
      {addMember && (
        <Model
          header="Add New Member"
          handleClose={handleMembers}
          Content={<Addmembers />}
        />
      )}
    </div>
  );
};

export default Member;
