import React from 'react'
import HomeIcon from '@mui/icons-material/Home';
import Groups3Icon from '@mui/icons-material/Groups3';
import LogoutIcon from '@mui/icons-material/Logout';
const Sidebar = ({ gymName, adminName }) => {
    const getGreeting = () => {
      const hour = new Date().getHours();
      if (hour < 12) return "Good Morning";
      if (hour < 18) return "Good Afternoon";
      return "Good Evening";
    };
  return (
    <div className='flex h-screen'>
      <div className="h-[100vh] w-80 bg-gray-900 text-white shadow-xl flex flex-col">
      {/* Gym Name & Admin */}
      <div className="p-6 text-center border-b border-gray-700 bg-white/10 backdrop-blur-md">
        <h1 className="text-2xl font-bold">GYM Mas</h1>
        <div className='flex gap-5 my-5'>
            <div className='w-[100px] h-[100px]'>
                <img alt='gym pic'  className='w-full h-full rounded-full' src='https://media.istockphoto.com/id/1438034462/photo/latino-and-african-sport-woman-exercising-and-build-muscle-in-stadium-active-strong-beautiful.jpg?s=612x612&w=0&k=20&c=kFwCRkh8Q1v6uCoSTL7sQcsbk02zgSZJ1kDgnJ3DAZc='/>

            </div>
           
        </div>
        <div className="text-xl text-gray-300">{getGreeting()} ,</div>
       <div className='text-right'> {adminName}</div>

      </div>
       
      {/* Navigation */}
      <nav className="flex-1 mt-10 ">
        <ul>
          <li className=" hover:text-black px-6 py-3 hover:bg-gradient-to-br from-blue-500 via-purple-500 to-indigo-600 transition rounded-lg cursor-pointer flex items-center gap-3">
          <span> <HomeIcon/></span>
            <span className="text-lg font-medium">Dashboard</span>
          </li>

          <li className=" hover:text-black  px-6 py-3 hover:bg-gradient-to-br from-blue-500 via-purple-500 to-indigo-600 transition rounded-lg cursor-pointer flex items-center gap-3">
            <span><Groups3Icon/></span>
            <span className="text-lg font-medium">Members</span>
          </li>
        </ul>
      </nav>

      {/* Logout Button */}
      <div className="mt-auto p-6 flex  ">
        
        <button className="w-full flex items-center justify-center gap-3 bg-red-600 hover:bg-red-700 text-white py-3 rounded-lg font-semibold transition duration-300">
        <LogoutIcon/>
          Logout
        </button>
      </div>
    </div>
    </div>
  )
}

export default Sidebar
