import React ,{useState} from 'react'
import Loader from '../Loader/loader'
import axios from 'axios'
import {toast,ToastContainer} from 'react-toastify';
function Forgetpassword() {
    const [emailSubmit,setEmailSubmit] = useState(false)
    const [otpValidate,setOtpValidate] =useState(false)
    const [loader,setLoader]=useState(false)
    const [contentVal,setContentValue] = useState("Submit Your Email ")

    const [inputField, setInputField] = useState({email: "", otp: "", newPassword: ""})

    const handleSubmit =()=>{
        if(!emailSubmit){
            //setEmailSubmit(true)
           // setContentValue("Submit Your OTP")
            sendOtp();
        }else if(emailSubmit && !otpValidate){
           //setOtpValidate(true)
           //setContentValue("Submit Your New Password")
        }
    }
     const sendOtp = async() => {
      await axios.post('http://localhost:4000/auth/reset-password/sendOtp',{email:inputField.email}).then((response)=>{
        
      }).catch(err=>{
                   const errorMessage = err.response.data.error
                 //console.log(errorMessage);
                  toast.error(errorMessage)
                
              })
     }

    const handleOnChange =(event,name)=>{
      setInputField({...inputField,[name]:event.target.value})
  }
  
  return (
    <div className="w-full">
  
    <p className="w-full mb-5">
      Enter your email to receive an OTP.
    </p>

    {/* Email Input */}
    <div className="mb-4">
      <label className="block text-sm font-semibold text-gray-950 mb-2">Email</label>
      <input
         value={inputField.email} onChange={(event)=>{handleOnChange(event,"email")}}
        type="email"
        placeholder="Enter your email"
        className="w-1/2 p-2 py-3 rounded-lg border-2 border-slate-400"
      />
    </div>
     {
        emailSubmit &&  <div className="mb-4">
        <label className="block text-sm font-semibold text-gray-950 mb-2">Enter OTP</label>
        <input
         value={inputField.otp} onChange={(event)=>{handleOnChange(event,"otp")}}
          type="text "
          placeholder="Enter your OTP"
          className="w-1/2 p-2 py-3 rounded-lg border-2 border-slate-400"
        />
      </div>
     }   
      {
        otpValidate &&  <div className="mb-4">
        <label className="block text-sm font-semibold text-gray-950 mb-2">Enter Your New Password</label>
        <input
        value={inputField.newPassword} onChange={(event)=>{handleOnChange(event,"newPassword")}}
          type='password'
          placeholder="Enter your New Password"
          className="w-1/2 p-2 py-3 rounded-lg border-2 border-slate-400"
        />
      </div>
     }     
    {/* Send OTP Button */}
    <div className="w-full text-center mt-4 py-3 bg-blue-600 hover:bg-blue-700 text-white font-semibold rounded-lg transition duration-300 shadow-md" onClick={() =>handleSubmit()}>{contentVal}</div>
     

    {/* Back to Login */}
    <p className="mt-4 text-center text-sm text-gray-800">
      Remember your password?{" "}
      <a href="/userLogin" className="text-blue-600 hover:underline">
        Login
      </a>
    </p>
    {loader && <Loader/>} 
</div>

  )
}

export default Forgetpassword
