import './App.css';
import Home from './pages/home/home';
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import UserLogin from './Components/Login/userLogin';
import Register from './Components/Signup/register';
import Dashboard from './pages/Dashboard/dashboard';
import Sidebar from './Components/Sidebar/sidebar';
import { useState, useEffect } from 'react';
import { useNavigate } from "react-router-dom"; // ✅ Import inside a component

function App() {
  const [isLogin, setIsLogin] = useState(false);

  return (
    <Router> {/* ✅ Router wraps the entire app */}
      <MainContent isLogin={isLogin} setIsLogin={setIsLogin} />
    </Router>
  );
}

function MainContent({ isLogin, setIsLogin }) {
  const navigate = useNavigate(); // ✅ useNavigate is now inside <Router>

  useEffect(() => {
    let isLogedIn = sessionStorage.getItem("isLogin");
    if (isLogedIn) {
      setIsLogin(true);
      navigate('/dashboard');
    }
  }, []); // ✅ Remove dependency on sessionStorage.getItem() to prevent infinite loops

  return (
    <div className="flex w-">
      {isLogin && <Sidebar />}

      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/userlogin" element={<UserLogin />} />
        <Route path="/register" element={<Register />} />
        <Route path="/dashboard" element={<Dashboard />} />
      </Routes>
    </div>
  );
}

export default App;
