import { useState, useEffect } from "react";
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer, Legend } from "recharts";
import axios from "axios";

export default function Graph() {
  const [totalMembers, setTotalMembers] = useState(0);
  const [totalIncome, setTotalIncome] = useState(0);
  const [revenueData, setRevenueData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const membersRes = await axios.get("http://localhost:4000/members/searched-members?searchTerm=", { withCredentials: true });
        const members = membersRes.data.members || [];
        setTotalMembers(members.length);

        let totalRevenue = 0;
        let totalIncomeCalc = 0;
        const revenueDataArray = [];

        for (const member of members) {
          if (member.membershipId) {
            const membershipRes = await axios.get(`http://localhost:4000/plans/get-membership/${member.membershipId}`, { withCredentials: true });
            const price = membershipRes.data?.price || 0;
            totalRevenue += price;
            totalIncomeCalc += price;
          }
        }

        setTotalIncome(totalIncomeCalc);

        revenueDataArray.push({ period: "Jan", members: members.length, revenue: totalRevenue });
        revenueDataArray.push({ period: "Feb", members: members.length + 5, revenue: totalRevenue * 1.1 });
        revenueDataArray.push({ period: "Mar", members: members.length + 10, revenue: totalRevenue * 1.2 });

        setRevenueData(revenueDataArray);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };
    fetchData();
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <h2>Total Members: {totalMembers}</h2>
      <h2>Estimated Revenue: ${revenueData.length > 0 ? revenueData[0].revenue.toFixed(2) : 0}</h2>
      <h2>Total Income: ${totalIncome.toFixed(2)}</h2>

      <div style={{ width: "100%", height: 300 }}>
        <ResponsiveContainer width="100%" height="100%">
          <BarChart data={revenueData}>
            <XAxis dataKey="period" stroke="#8884d8" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Bar dataKey="members" fill="#2563EB" barSize={40} />
            <Bar dataKey="revenue" fill="#4F46E5" barSize={40} />
          </BarChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
}
