import React from "react";

const Home = () => {
  return (
    <div className="bg-gradient-to-br from-blue-500 via-purple-500 to-indigo-600 min-h-screen flex flex-col justify-start items-center pt-12 relative">
      {/* Background Image */}
      <div className="absolute inset-0 bg-[url('https://as2.ftcdn.net/v2/jpg/06/74/96/46/1000_F_674964630_CeZ2cAvyJ3uUoBAxJ4czwnIo8TVXxguB.jpg')] bg-cover bg-center brightness-75"></div>

      {/* Overlay Content */}
      <div className="relative text-center text-white px-6 max-w-2xl z-10">
        <h1 className="text-3xl md:text-5xl font-bold drop-shadow-lg animate-fade-in">
          Welcome to <span className="text-yellow-400">GYM PRO</span> Management System
        </h1>
        <p className="mt-4 text-lg md:text-xl opacity-90 animate-fade-in delay-200">
          Track, Train, and Transform â€“ Your fitness journey starts here!
        </p>
      </div>

      {/* Main Container for Login & Content */}
      <div className="relative w-full flex flex-col lg:flex-row items-center lg:items-start justify-between px-10 z-10 mt-12">
        
        {/* Left Side - Login Form */}
        <div className="w-full max-w-md p-8 bg-white shadow-lg rounded-lg">
          <h2 className="text-3xl font-bold text-gray-900 mb-6">Welcome Back</h2>

          {/* Username Input */}
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-semibold mb-2">
              Username
            </label>
            <input
              type="text"
              placeholder="Enter your username"
              className="w-full px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 shadow-sm"
            />
          </div>

          {/* Password Input */}
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-semibold mb-2">
              Password
            </label>
            <input
              type="password"
              placeholder="Enter your password"
              className="w-full px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 shadow-sm"
            />
          </div>

          {/* Login Button */}
          <button className="w-full bg-blue-600 text-white py-3 rounded-lg font-semibold hover:bg-blue-700 transition duration-300">
            Login
          </button>

          {/* Forgot Password & Signup */}
          
        </div>

      </div>
    </div>
  );
};

export default Home;
