import React ,{useState} from "react";
import ArrowBackIcon from "@mui/icons-material/ArrowBack";
import { useNavigate } from "react-router-dom";
import Switch from 'react-switch';
const MemberDetail = () => {
  const[status,setStatus] = useState("Pending");
  const [renew, setRenew] = useState(false);
  const navigate = useNavigate();
  const handleSwitchBtn = async() => {
      let statuss = status === "Active" ? "Pending" : "Active";
      setStatus(statuss);
    }
  return (
    <div className="w-3/4 text-black p-5">
      <div
        onClick={() => {
          navigate(-1);
        }}
        className="border-2 w-fit text-xl font-sans text-white p-2 rounded-xl bg-slate-900 cursor-pointer"
      >
        <ArrowBackIcon /> Go Back
      </div>
      <div className="mt-10 p-2">
        <div className="w-[100%] h-fit flex">
          <div className="w-1/3  mx-auto">
          <img src="https://imgs.search.brave.com/CgafHex2_xO6IM4K5CsF4cNrpAKbLPpAEFYC_iKot5E/rs:fit:860:0:0:0/g:ce/aHR0cHM6Ly90NC5m/dGNkbi5uZXQvanBn/LzA2LzIxLzUwLzQ3/LzM2MF9GXzYyMTUw/NDczOV9vNGlYMndU/dTNGR21ZTVlYSUM2/dk5FdlR0VHZ2WFo3/RC5qcGc"className="w-full  mx-auto"/>
      
          </div>
          <div className="w-2/3 mt-5 text-xl p-5">
           <div className="mt-1 mb-2 text-2xl  font-semibold">Name: Sinu Das</div>
           <div className="mt-1 mb-2 text-2xl  font-semibold">Mobile No: +918144674412</div>
           <div className="mt-1 mb-2 text-2xl  font-semibold">Address: JSG,Odisha</div>
           <div className="mt-1 mb-2 text-2xl  font-semibold">Join Date: 01-02-2025</div>
           <div className="mt-1 mb-2 text-2xl  font-semibold">Next Bill Date: 28-02-2025</div>
           <div className="mt-1 mb-2 flex gap-4 text-2xl font-semibold">Status:<Switch onColor="#6366F1" checked={status=="Active"} onChange={()=>{handleSwitchBtn()}}/></div>
           <div onClick={()=>{setRenew(prev => !prev)}} className={`mt-1 rounded-lg p-3 border-2 border-slate-900 text-center ${renew && status==="Active"?'text-white bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500':null} w-full md:w-1/2 cursor-pointer hover:text-white hover:bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500`}> Renew</div>
           </div>
        </div>

        <div className="rounded-lg p-3 mt-5 mx-auto mb-5 h-fit bg-slate-50 md:w-[50%]">
         <div className="">
          <div>

          </div>
         </div>

        </div>

      </div>
    </div>
  );
};

export default MemberDetail;
