import React, { useState, useEffect } from "react";
import axios from "axios";
import Box from "@mui/material/Box";
import LinearProgress from "@mui/material/LinearProgress";
import { toast, ToastContainer } from "react-toastify";

const Addmembers = () => {
  const [inputField, setInputField] = useState({
    name: "",
    mobileNo: "",
    address: "",
    membership: "",
    profilePic:
      "https://imgs.search.brave.com/D9s_Ligx0yusV1Qg7hijgsU7hu7YV8Lgs9mzKbiu0k8/rs:fit:500:0:0:0/g:ce/aHR0cHM6Ly9pbWcu/ZnJlZXBpay5jb20v/cHJlbWl1bS1waG90/by9tYW4tc3RhbmRp/bmctZ3ltLXdpdGgt/aGlzLWFybXMtY3Jv/c3NlZF85NDY1ODUt/NjgzOS5qcGc_c2Vt/dD1haXNfaHlicmlk",
    joiningDate: "",
  });

  const [loaderImage, setLoaderImage] = useState(false);
  const [membershipList, setMembershipList] = useState([]);
  const [selectedOption, setSelectedOption] = useState("");

  // ✅ Ensure the input fields update safely
  const handleOnChange = (event, name) => {
    setInputField((prev) => ({ ...prev, [name]: event.target.value }));
  };

  // ✅ Fix: Handle Image Upload
  const uploadImage = async (event) => {
    setLoaderImage(true);
    console.log("Image uploading...");
    const files = event.target.files;
    if (!files || files.length === 0) {
      console.log("No file selected.");
      setLoaderImage(false);
      return;
    }

    const data = new FormData();
    data.append("file", files[0]);
    data.append("upload_preset", "gym-management");

    try {
      const response = await axios.post(
        "https://api.cloudinary.com/v1_1/dsscj2vni/image/upload",
        data
      );
      console.log("Upload Success:", response.data);

      const imageUrl = response.data.url;
      setInputField((prev) => ({ ...prev, profilePic: imageUrl }));
    } catch (error) {
      console.error("Upload failed:", error.response?.data || error.message);
      toast.error("Image upload failed!");
    } finally {
      setLoaderImage(false);
    }
  };

  // ✅ Fix: Fetch Membership List Safely
  const fetchMembership = async () => {
    try {
      const response = await axios.get(
        "http://localhost:4000/plans/get-membership",
        { withCredentials: true }
      );
      const memberships = response.data.membership;

      if (memberships.length === 0) {
        toast.error("No membership added yet", { className: "text-lg" });
      } else {
        const defaultMembership = memberships[0]._id;
        setMembershipList(memberships);
        setSelectedOption(defaultMembership);
        setInputField((prev) => ({ ...prev, membership: defaultMembership }));
      }
    } catch (error) {
      console.log(error);
      toast.error("Something went wrong while fetching memberships.");
    }
  };

  useEffect(() => {
    fetchMembership();
  }, []);

  // ✅ Fix: Ensure event is passed safely
  const handleOnChangeSelect = (event) => {
    if (!event || !event.target) {
      console.error("handleOnChangeSelect: event is undefined");
      return;
    }
    const value = event.target.value;
    setSelectedOption(value);
    setInputField((prev) => ({ ...prev, membership: value }));
  };

  return (
    <div className="text-black">
      <div className="grid gap-5 grid-cols-2 text-lg">
        <input
          value={inputField.name}
          onChange={(event) => handleOnChange(event, "name")}
          placeholder="Name of the joinee"
          type="text"
          className="border-2 w-[90%] pl-3 pr-3 pt-2 pb-2 border-slate-400 rounded-md h-12"
        />
        <input
          value={inputField.mobileNo}
          onChange={(event) => handleOnChange(event, "mobileNo")}
          placeholder="Mobile number"
          type="text"
          className="border-2 w-[90%] pl-3 pr-3 pt-2 pb-2 border-slate-400 rounded-md h-12"
        />
        <input
          value={inputField.address}
          onChange={(event) => handleOnChange(event, "address")}
          placeholder="Enter Address"
          type="text"
          className="border-2 w-[90%] pl-3 pr-3 pt-2 pb-2 border-slate-400 rounded-md h-12"
        />
        <input
          value={inputField.joiningDate}
          onChange={(event) => handleOnChange(event, "joiningDate")}
          type="date"
          className="border-2 w-[90%] pl-3 pr-3 pt-2 pb-2 border-slate-400 rounded-md h-12"
        />

        {membershipList.length > 0 ? (
          <select
            value={selectedOption}
            onChange={handleOnChangeSelect}
            className="border-2 w-[90%] b-12 pt-2 pb-2 border-slate-400 rounded-md placeholder:text-gray"
          >
            {membershipList.map((item, index) => (
              <option key={index} value={item._id}>
                {item.months} months Membership
              </option>
            ))}
          </select>
        ) : (
          <p className="text-red-500">No memberships available</p>
        )}

        <input type="file" onChange={uploadImage} className="" />

        <div className="w-[100px] h-[100px]">
          <img src={inputField.profilePic} className="w-full h-full rounded-full" />
          {loaderImage && (
            <Box sx={{ width: "100%" }}>
              <LinearProgress />
            </Box>
          )}
        </div>

        <div className="p-3 border-2 w-28 text-lg h-14 text-center bg-slate-900 text-white rounded-xl cursor-pointer hover:bg-gradient-to-r from-indigo-500 via-purple-400 to-pink-400">
          Register
        </div>
      </div>
      <ToastContainer />
    </div>
  );
};

export default Addmembers;
