import React,{useState} from 'react'
import Model from '../Model/model'

function UserLogin() {
   const [forgetPassword,setforgetPassword]=useState(false);
    const handleClose =()=>{
      setforgetPassword(prev=>!prev);
    }
    const forgetPasswordContent=()=>{
      return(
        <div className="forget-password-container">
                   this 
          <div className=""></div>
          </div>
      )
    }
  return (
    <div>
      {/* Main Container for Login & Register */}
      <div className="bg-gradient-to-br from-blue-500 via-purple-500 to-indigo-600 min-h-screen flex flex-col justify-start items-center pt-12 relative ">
        
        {/* Left Side - Login Form */}
        <div className="w-full max-w-md p-8 bg-gray-50 shadow-lg rounded-lg text-center">
          <h2 className="text-3xl font-bold text-gray-900 mb-6">USER LOGIN</h2>

          {/* Username Input */}
          <div className="mb-4 text-left">
            <label className="block text-gray-700 text-sm font-semibold mb-2">
              Username
            </label>
            <input
              type="text"
              placeholder="Enter your username"
              className="w-full px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 shadow-sm"
            />
          </div>

          {/* Password Input */}
          <div className="mb-4 text-left">
            <label className="block text-gray-700 text-sm font-semibold mb-2">
              Password
            </label>
            <input
              type="password"
              placeholder="Enter your password"
              className="w-full px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 shadow-sm"
            />
          </div>

          {/* Login Button */}
          <button className="w-full bg-blue-600 text-white py-3 rounded-lg font-semibold hover:bg-blue-700 transition duration-300">
            Login
          </button>

          {/* Forgot Password & Signup */}
          <div className="mt-4 text-sm text-gray-600 flex justify-between">
            <a href="#" className="hover:text-blue-600" onClick={()=>handleClose()}>Forgot password?</a>
            <a href="/register" className="hover:text-blue-600">Sign up</a>
          </div>
        </div>

        
      </div>
      {forgetPassword &&<Model handleClose={handleClose}/>}
    </div>
  )
}

export default UserLogin
