import React,{useState} from 'react'
import Model from '../Model/model'
import Forgetpassword from '../ForgetPassword/forgetpassword';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import {toast,ToastContainer} from 'react-toastify';

function UserLogin() {
  const [loginField,setLoginField] = useState({"userName":"","password":""});
  const navigate = useNavigate();

  const handleLogin = async()=>{
   // sessionStorage.setItem("isLogin",true)
   // navigate('/dashboard')
    await axios.post('http://localhost:4000/auth/login',loginField,{withCredentials:true}).then((response)=>{
        console.log(response.data);
        localStorage.setItem('gymName',response.data.gym.gymName);
        localStorage.setItem('gymPic',response.data.gym.profilePic);
        localStorage.setItem('isLogin',true);
        localStorage.setItem('token',response.data.token);

        navigate('/dashboard')

    }).catch(err=>{
         const errorMessage = err.response.data.error
       //console.log(errorMessage);
        toast.error(errorMessage)
      
    })
   }
   const handleSignup =()=>{
    navigate('/register')
   }
   const [forgetPassword,setforgetPassword]=useState(false);
    const handleClose =()=>{
      setforgetPassword(prev=>!prev);
    }
    const handleOnChange =(event,name)=>{
      setLoginField({...loginField,[name]:event.target.value});
  }
  
  return (
    <div>
      {/* Main Container for Login & Register */}
      <div className="w-screen h-screen bg-gradient-to-br from-blue-500 via-purple-500 to-indigo-600 min-h-screen  flex  flex-col justify-center   items-center pt-12 relative">
        
        {/* Left Side - Login Form */}
        <div className="w-full    max-w-md p-8 bg-gray-50 shadow-lg rounded-lg text-center">
          <h2 className="text-3xl font-bold text-gray-900 mb-6">USER LOGIN</h2>

          {/* Username Input */}
          <div className="mb-4 text-left">
            <label className="block text-gray-700 text-sm font-semibold mb-2">
              Username
            </label>
            <input
            value={loginField.userName} onChange={(event)=>{handleOnChange(event,"userName")}}
              type="text"
              placeholder="Enter your username"
              className="w-full px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 shadow-sm"
            />
          </div>

          {/* Password Input */}
          <div className="mb-4 text-left">
            <label className="block text-gray-700 text-sm font-semibold mb-2">
              Password
            </label>
            <input
            value={loginField.password}  onChange={(event)=>{handleOnChange(event,"password")}}
              type="password"
              placeholder="Enter your password"
              className="w-full px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 shadow-sm"
            />
          </div>

          {/* Login Button */}
          <div className="w-full bg-blue-600 text-white py-3 rounded-lg font-semibold hover:bg-blue-700 transition duration-300" onClick={()=>{handleLogin()}}>
            Login
          </div>

          {/* Forgot Password & Signup */}
          <div className="mt-4 text-sm text-gray-600 flex justify-between">
            <div className="hover:text-blue-600" onClick={()=>handleClose()}>Forgot password?</div>
            <div  onClick={()=>{handleSignup()}} className="hover:text-blue-600">Sign up</div>
          </div>
        </div>

        
      </div>
      {forgetPassword &&<Model header="Forgot password" handleClose={handleClose} Content={<Forgetpassword/>}/>}
      <ToastContainer/>
    </div>
  )
}

export default UserLogin
