import React from 'react'
import HomeIcon from '@mui/icons-material/Home';
const Sidebar = ({ gymName, adminName }) => {
    const getGreeting = () => {
      const hour = new Date().getHours();
      if (hour < 12) return "Good Morning";
      if (hour < 18) return "Good Afternoon";
      return "Good Evening";
    };
  return (
    <div>
      <div className="h-screen w-1/4 bg-gray-900 text-white shadow-xl flex flex-col">
      {/* Gym Name & Admin */}
      <div className="p-6 text-center border-b border-gray-700 bg-white/10 backdrop-blur-md">
        <h1 className="text-2xl font-bold">{gymName}</h1>
        <div className='flex gap-5 my-5'>
            <div className='w-[100px] h-[100px]'>
                <img alt='gym pic'  className='w-full h-full rounded-full' src='https://media.istockphoto.com/id/1438034462/photo/latino-and-african-sport-woman-exercising-and-build-muscle-in-stadium-active-strong-beautiful.jpg?s=612x612&w=0&k=20&c=kFwCRkh8Q1v6uCoSTL7sQcsbk02zgSZJ1kDgnJ3DAZc='/>

            </div>
            <div><p className="text-2xl text-gray-300">{getGreeting()}</p></div>
        </div>
        
       <div className='text-right'> {adminName}</div>

      </div>
       
      {/* Navigation */}
      <nav className="flex-1 mt-10 border-t-2">
        <ul>
          <li className="px-6 py-3 hover:bg-gradient-to-br from-blue-500 via-purple-500 to-indigo-600 transition rounded-lg cursor-pointer flex items-center gap-3">
          <span> <HomeIcon/></span>
            <span className="text-lg font-medium">Dashboard</span>
          </li>

          <li className="  px-6 py-3 hover:bg-gradient-to-br from-blue-500 via-purple-500 to-indigo-600 transition rounded-lg cursor-pointer flex items-center gap-3">
            <span> icon</span>
            <span className="text-lg font-medium">Members</span>
          </li>
        </ul>
      </nav>

      {/* Logout Button */}
      <div className="mt-auto p-6">
        <button className="w-full flex items-center justify-center gap-3 bg-red-600 hover:bg-red-700 text-white py-3 rounded-lg font-semibold transition duration-300">
       
          Logout
        </button>
      </div>
    </div>
    </div>
  )
}

export default Sidebar
