import React from 'react'

const Sidebar = ({ gymName, adminName }) => {
    const getGreeting = () => {
      const hour = new Date().getHours();
      if (hour < 12) return "Good Morning";
      if (hour < 18) return "Good Afternoon";
      return "Good Evening";
    };
  return (
    <div>
      <div className="h-screen w-64 bg-gray-900 text-white shadow-xl flex flex-col">
      {/* Gym Name & Admin */}
      <div className="p-6 text-center border-b border-gray-700 bg-white/10 backdrop-blur-md">
        <h1 className="text-2xl font-bold">{gymName}</h1>
        <div className='flex gap-5'>
            <div className='w'>

            </div>
        </div>
        <p className="text-sm text-gray-300">{getGreeting()}, {adminName} </p>
      </div>

      {/* Navigation */}
      <nav className="flex-1 mt-6">
        <ul>
          <li className="px-6 py-3 hover:bg-blue-600 transition rounded-lg cursor-pointer flex items-center gap-3">
           
            <span className="text-lg font-medium">Dashboard</span>
          </li>

          <li className="px-6 py-3 hover:bg-blue-600 transition rounded-lg cursor-pointer flex items-center gap-3">
            
            <span className="text-lg font-medium">Members</span>
          </li>
        </ul>
      </nav>

      {/* Logout Button */}
      <div className="mt-auto p-6">
        <button className="w-full flex items-center justify-center gap-3 bg-red-600 hover:bg-red-700 text-white py-3 rounded-lg font-semibold transition duration-300">
       
          Logout
        </button>
      </div>
    </div>
    </div>
  )
}

export default Sidebar
