      {/* Background Image */}
      <div className="absolute inset-0 bg-[url('https://files.oaiusercontent.com/file-41HweKCX1YfK9kCC4tDHLS?se=2025-03-13T06%3A58%3A09Z&sp=r&sv=2024-08-04&sr=b&rscc=max-age%3D604800%2C%20immutable%2C%20private&rscd=attachment%3B%20filename%3D250b33ee-47fa-41fb-8ae0-76d58d203fee.webp&sig=kN6SU5sx2zaAc9sElvyCrwpdWi4t4O5IVfP6cuxy4q0%3D')] bg-cover bg-center brightness-75"></div>

      {/* Overlay Content */}
      
      <div className="relative text-center text-white px-6 max-w-2xl z-10  mt-20 justify-center">
        <h1 className="text-3xl md:text-5xl font-bold drop-shadow-lg animate-fade-in">
          Welcome to <span className="text-yellow-400">GYM PRO</span> Management System
        </h1>
        <p className="mt-4 text-lg md:text-xl opacity-90 animate-fade-in delay-200 text-red">
          Track, Train, and Transform â€“ Your fitness journey starts here!
        </p>
         <div className="mt-6 p-5 px-6 py-3">
        <Link to={'/userLogin'} className="mt-6 p-5 px-6 py-3 bg-yellow-400 text-black font-semibold rounded-lg shadow-lg hover:bg-yellow-500 transition duration-300 animate-fade-in delay-400">
        GET Started
        
        </Link>
        </div>
      </div>
      
      
    </div>
    
  );
};

export default Home;
