import React, { useState, useEffect } from "react";
import FitnessCenterIcon from "@mui/icons-material/FitnessCenter";
import AddIcon from "@mui/icons-material/Add";
import { Link } from "react-router-dom";
import ArrowBackIcon from "@mui/icons-material/ArrowBack";
import SearchIcon from "@mui/icons-material/Search";
import ChevronLeftIcon from "@mui/icons-material/ChevronLeft";
import ChevronRightIcon from "@mui/icons-material/ChevronRight";
import Model from "../../Components/Model/model";
import MemberCard from "../../Components/MemberCard/memberCard";
import AddmemberShip from "../../Components/Addmembership/addmemberShip";
import Addmembers from "../../Components/Addmembers/addmembers";
import axios from "axios";
import { toast, ToastContainer } from "react-toastify";

const Member = () => {
  const [addMembership, setAddmemberShip] = useState(false);
  const [addMember, setAddmember] = useState(false) 
  const [data, setData] = useState([]);
  const [skip, setSkip] = useState(0);
  const [search, setSearch] = useState("");
  const [isSearchModeOn, setIsSearchModeOn] = useState(false)

  const [currentPage, setCurrentPage] = useState(1);

  const [startFrom, setSTartFrom] = useState(0);
  const [endTo, setEndTo] = useState(9);
  const [totalData, setTotalData] = useState(0);
  const [limit, setLimit] = useState(9);

  const [noOfPage, setNoOfPage] = useState(0);
   
  useEffect(() => {
    fetchData(0,9);
    }, [])

    const fetchData = async (skip,limits) => {
       await axios.get(`http://localhost:4000/members/all-member?skip=${skip}&limit=${limits}`,{withCredentials:true}).then((response)=>{
           console.log(response);
           const totalMembers = response.data.totalMembers;
          setTotalData(totalMembers);
          setData(response.data.members);

           let extraPage = totalMembers % limit === 0 ? 0 : 1;
           let totalPages = Math.floor(totalMembers / limit) + extraPage;
          setNoOfPage(totalPages);

         if (totalMembers === 0) {
        setStartFrom(-1);
        setEndTo(0);
      } else {
        setStartFrom(skip);
        setEndTo(Math.min(skip + limit, totalMembers));
      }
    
       })
    
  }


// Handle fetching members (dummy function, replace with actual logic)
const handleMembers = () => {
  setAddmember(prev => !prev);
};

// Handle fetching memberships (dummy function, replace with actual logic)
const handleMemberShip = () => {
  setAddmemberShip(prev => !prev);
};

// Handle search functionality
const handleSearchData = () => {
  setIsSearchModeOn(search.length > 0);
  console.log("Searching for:", search);
};

// Handle previous page in pagination
const handlePrev = () => {
  if (currentPage !== 1) {
    let currPage=currentPage - 1;
    setCurrentPage(currPage);
    var from=(currPage - 1) * 9;
    var to=(currPage * 9)
    setSTartFrom(from)
    setEndTo(to);
    let skipValue = skip-9;
    setSkip(skipValue);
    fetchData(skipValue,9);
    
  }
};

// Handle next page in pagination
const handleNext = () => {
  if (currentPage !== noOfPage) {
    let currPage=currentPage + 1;
    setCurrentPage(currPage);
    var from=(currPage - 1) *9;
    var to=(currPage * 9)
    if(to > totalData){
      to = totalData;
    }
    setSTartFrom(from)
    setEndTo(to);
    let skipValue = skip+9;
    setSkip(skipValue);
    fetchData(skipValue,9);
  }
};

  return (
    <div className="text-black p-5 w-3/4 h-[100vh]">
      {/* block for banner */}
      <div className="border-2 bg-gray-800 flex justify-between w-full text-white rounded-lg p-3">
        <div
          className="border-2 pl-3 pr-3 pt-1 pb-1 rounded-2xl cursor-pointer hover:bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500 hover:text-black"
          onClick={() => handleMembers()}
        >
          Add Member <FitnessCenterIcon />{" "}
        </div>
        <div
          className="border-2 pl-3 pr-3 pt-1 pb-1 rounded-2xl cursor-pointer hover:bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500 hover:text-black"
          onClick={() => handleMemberShip()}
        >
          Membership <AddIcon />
          
        </div>
      </div>

      {/* block for back to dashboard button */}
      <Link to={"/dashboard"}>
        <ArrowBackIcon /> Back to Dashboard{" "}
      </Link>

      <div className="mt-5 w-1/2 flex gap-2">
        <input
          type="text"
          value={search}
          onChange={(e) => {
            setSearch(e.target.value);
          }}
          className="border-2 w-full p-2 rounded-lg"
          placeholder="Search By Name or Mobile No"
        />
        <div
          onClick={() => {
            handleSearchData();
          }}
          className="bg-slate-900 p-3 border-2 text-white rounded-lg cursor-pointer hover:bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500 hover:text-black"
        >
          <SearchIcon />
        </div>
      </div>

      <div className="mt-5 text-xl flex justify-between text-slate-900">
        <div>Total Members {isSearchModeOn ? totalData : null}</div>
        {!isSearchModeOn ? (
          <div className="flex gap-5">
            <div>
              {startFrom +1} - {endTo} of {totalData} Members
            </div>
            <div
              className={`w-8 h-8 cursor-pointer border-2 flex items-center justify-center hover:text-white hover:bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500 ${
                currentPage === 1 ? "bg-gray-200 text-gray-400" : null
              }`}
              onClick={() => {
                handlePrev();
              }}
            >
              <ChevronLeftIcon />
            </div>
            <div
              className={`w-8 h-8 cursor-pointer border-2 flex items-center justify-center hover:text-white hover:bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500 ${
                currentPage === noOfPage ? "bg-gray-200 text-gray-400" : null
              }`}
              onClick={() => {
                handleNext();
              }}
            >
              <ChevronRightIcon />
            </div>
          </div>
        ) : null}
      </div>

      <div className="bg-slate-100 p-5 mt-5 rounded-lg grid gap-2 grid-cols-3 overflow-x-auto h-[65%]">
        {/* div for memver card*/}
          {
            data.map((item,index)=>{
              return(
                <MemberCard item={item}/>
              );
            })
          }
          
          
         
      </div>
      {addMembership && <Model header="Add Membership" handleClose={handleMemberShip} Content={<AddmemberShip handleClose={handleMemberShip}/>}/>}
      {addMember && <Model header={"Add New Member"} handleClose={handleMembers} Content={<Addmembers/>}/>}
    </div>
  );
};

export default Member;
