import './App.css';
import { Routes, Route, useNavigate, useLocation } from "react-router-dom";
import { useState, useEffect } from 'react';

import Home from './pages/home/home';
import UserLogin from './Components/Login/userLogin';
import Register from './Components/Signup/register';
import Dashboard from './pages/Dashboard/dashboard';
import Sidebar from './Components/Sidebar/sidebar';

function App() {
  const navigate = useNavigate();
  const location = useLocation(); // Get the current route
  const [isLogin, setIsLogin] = useState(false);

  // Check login status on page load
  useEffect(() => {
    const isLoggedIn = sessionStorage.getItem("isLogin") === "true";
    setIsLogin(isLoggedIn);

    if (isLoggedIn) {
      navigate('/dashboard'); // Redirect only if logged in
    }
  }, []);

  return (
    <div className="flex h-screen">
      {/* Show Sidebar only on the Dashboard page */}
      {isLogin && location.pathname === "/dashboard" && <Sidebar />}

      <div className="flex-grow transition-all duration-300">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/dashboard" element={<Dashboard />} />
          <Route path="/userlogin" element={<UserLogin setIsLogin={setIsLogin} />} />
          <Route path="/register" element={<Register />} />
        </Routes>
      </div>
    </div>
  );
}

export default App;
