import { useState, useEffect } from "react";
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer, Legend } from "recharts";
import axios from "axios";

export default function Graph() {
  const [totalMembers, setTotalMembers] = useState(0);
  const [membershipPrice, setMembershipPrice] = useState(0);
  const [revenueData, setRevenueData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const membersRes = await axios.get(`http://localhost:4000/members/searched-members?searchTerm=`, { withCredentials: true });
        const membershipsRes = await axios.get("http://localhost:4000/plans/get-membership", { withCredentials: true });

        // Ensure data exists
        const membersCount = membersRes.data?.totalMember || 0;
        const price = membershipsRes.data?.price || 0;

        setTotalMembers(membersCount);
        setMembershipPrice(price);

        // Calculate revenue
        const calculatedRevenue = membersCount * price;

        // Generate chart data
        setRevenueData([
          { period: "Jan", members: membersCount, revenue: calculatedRevenue },
          { period: "Feb", members: membersCount + 5, revenue: calculatedRevenue * 1.1 },
          { period: "Mar", members: membersCount + 10, revenue: calculatedRevenue * 1.2 },
        ]);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };
    fetchData();
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <h2>Total Members: {totalMembers}</h2>
      <h2>Membership Price: ${membershipPrice}</h2>
      <h2>Estimated Revenue: ${totalMembers * membershipPrice}</h2>

      <div style={{ width: "100%", height: 300 }}>
        <ResponsiveContainer width="100%" height="100%">
          <BarChart data={revenueData}>
            <XAxis dataKey="period" stroke="#8884d8" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Bar dataKey="members" fill="#2563EB" barSize={40} />
            <Bar dataKey="revenue" fill="#4F46E5" barSize={40} />
          </BarChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
}
